/**
 * Bulk review data for quota testing
 * Generate multiple reviews to test daily limit (10 per day)
 */
export function generateBulkReviews(count) {
  const reviews = [];
  const ratings = [1, 2, 3, 4, 5];
  const contents = [
    '–û—Ç–ª–∏—á–Ω–æ',
    '–•–æ—Ä–æ—à–æ',
    '–ù–æ—Ä–º–∞–ª—å–Ω–æ',
    '–ü–ª–æ—Ö–æ',
    '–£–∂–∞—Å–Ω–æ',
    '–ü–æ–Ω—Ä–∞–≤–∏–ª–æ—Å—å',
    '–ù–µ –ø–æ–Ω—Ä–∞–≤–∏–ª–æ—Å—å',
    '–†–µ–∫–æ–º–µ–Ω–¥—É—é',
    '–ù–µ —Ä–µ–∫–æ–º–µ–Ω–¥—É—é',
    '–°—Ä–µ–¥–Ω–µ'
  ];

  for (let i = 0; i < count; i++) {
    reviews.push({
      rating: ratings[i % ratings.length],
      content: `${contents[i % contents.length]} - –æ—Ç–∑—ã–≤ –Ω–æ–º–µ—Ä ${i + 1}`
    });
  }

  return reviews;
}

